import { Privacy } from "@fonoster/core";
export interface IAgentsClient {
    createAgent(request: CreateAgentRequest): Promise<CreateAgentResponse>;
    getAgent(ref: string): Promise<GetAgentResponse>;
    updateAgent(request: UpdateAgentRequest): Promise<UpdateAgentResponse>;
    listAgents(request: ListAgentsRequest): Promise<ListAgentsResponse>;
    deleteAgent(ref: string): Promise<DeleteAgentResponse>;
}
export interface Agent {
    ref: string;
    name: string;
    username: string;
    secret: string;
    domains: string[];
    privacy: Privacy;
    createTime: string;
    updateTime: string;
}
export interface CreateAgentRequest {
    name: string;
    username: string;
    secret: string;
    domains: string[];
    privacy?: Privacy;
}
export interface CreateAgentResponse {
    ref: string;
    name: string;
    username: string;
    secret: string;
    domains: string[];
    privacy: Privacy;
    createTime: string;
    updateTime: string;
}
export interface GetAgentResponse {
    ref: string;
    name: string;
    username: string;
    secret: string;
    domains: string[];
    privacy: Privacy;
    createTime: string;
    updateTime: string;
}
export interface UpdateAgentRequest {
    ref: string;
    name?: string;
    secret?: string;
    privacy?: Privacy;
}
export interface UpdateAgentResponse {
    ref: string;
}
export interface ListAgentsRequest {
    pageSize?: number;
    pageToken?: string;
    view?: number;
}
export interface ListAgentsResponse {
    nextPageToken: string;
    agents: Agent[];
}
export interface DeleteAgentResponse {
    ref: string;
}
